input[type="checkbox"] {
    appearance: none;
    width: 12px;
    height: 12px;
    border-radius: 2px;
    border: 1px solid #989898;
    position: relative;

    &:checked {
        border: 1px solid #006F51;
    }

    &:checked::before {
        content: '';
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
        width: 60%;
        height: 60%;
        background-color: #006F51;
    }

}

