// Custom add to cart qty boxes and price hiding

.custom-add-to-cart {
    display: flex;
    justify-content: space-between;
    padding-top: 30px;

    .alertBox {
        margin: auto;
    }

    .alertBox-icon {
        padding-right: 10px;
    }

    p {
        text-align: center;
        margin-bottom: 10px;
        font-size: 16px;
    }

    .input-box {
        display: flex;

        button {
            height: 30px;
            width: 32px;
            border: 1px solid #989898;
            color: #989898;
            z-index: 2;
            position: relative;

            &.dec {
                border-bottom-left-radius: 6px;
                border-top-left-radius: 6px;
            }

            &.inc {
                border-bottom-right-radius: 6px;
                border-top-right-radius: 6px;

                &:after {
                    position: absolute;
                    content: '';
                    height: 11px;
                    width: 1px;
                    background-color: #989898;
                    top: 50%;
                    left: 50%;
                    transform: translate(-50%, -50%);
                }
            }

            &:before {
                position: absolute;
                content: '';
                height: 1px;
                width: 11px;
                background-color: #989898;
                top: 50%;
                left: 50%;
                transform: translate(-50%, -50%);
            }
        }

        input {
            width: 50px;
            border-radius: 0;
            border: 1px solid #989898;
            border-left: 0;
            border-right: 0;
            text-align: center;
            color: #4F4F4F;
            font-size: 16px;
        }

        input::-webkit-outer-spin-button,
        input::-webkit-inner-spin-button {
            -webkit-appearance: none;
            margin: 0;
        }
    }
}

